package ITAcademy.june.day5;

public class Op3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b=20;
		
		System.out.println( a == b );   //false
		System.out.println( a != b ); //true 
		System.out.println( a > b );    //false
		System.out.println( a >= b );   //false
		System.out.println( a < b );    //true
		System.out.println( a <= b );   //true
		
	}

}
